mapper.insert:
insert into b_data_item_info(class_code, class_name, code, name, param, flag,
                             create_id, create_time, update_id, update_time)
values ( #{classCode,jdbcType=VARCHAR}, #{className,jdbcType=VARCHAR}, #{code,jdbcType=VARCHAR}
       , #{name,jdbcType=VARCHAR}, #{param,jdbcType=VARCHAR}, #{classCode,jdbcType=VARCHAR}
       , #{createId,jdbcType=INTEGER}, #{createTime,jdbcType=VARCHAR}
       , #{updateId,jdbcType=INTEGER}, #{updateTime,jdbcType=VARCHAR})

mapper.update:
update b_data_item_info
set class_code = #{classCode},
    class_name = #{className},
    code=#{code},
    name=#{name}
where id = #{id}

mapper.delete:
update b_data_item_info
set delete_flag=1
where id = #{id}
