mapper.insert:
insert into s_sys_org(parent, org_name, org_code, type, sort, area, grade, status, address,
                      security_key, create_id, create_time, update_id, update_time)
values ( #{parent,jdbcType=INTEGER}, #{orgName,jdbcType=VARCHAR}, #{orgCode,jdbcType=VARCHAR}
       , #{type,jdbcType=INTEGER}, #{sort,jdbcType=INTEGER}, #{area,jdbcType=VARCHAR}
       , #{grade,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}, #{address,jdbcType=VARCHAR}
       , #{securityKey,jdbcType=VARCHAR}, #{createId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
       , #{updateId,jdbcType=INTEGER}, #{updateTime,jdbcType=TIMESTAMP})

mapper.update:
update s_sys_org
set org_name    = #{orgName},
    org_code    = #{orgCode},
    address=#{address},
    security_key=#{securityKey}
where id = #{id}

mapper.delete:
update s_sys_org
set delete_flag=1
where id = #{id}
