mapper.insert:
insert into s_sys_permission(parent, name, permission, sort, href, target, icon, is_show, comment, status,
                             create_id, create_time, update_id, update_time)
values ( #{parent,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{permission,jdbcType=VARCHAR}
       , #{sort,jdbcType=INTEGER}, #{href,jdbcType=VARCHAR}, #{target,jdbcType=VARCHAR}
       , #{icon,jdbcType=VARCHAR}, #{isShow,jdbcType=INTEGER}, #{comment,jdbcType=VARCHAR}
       , #{status,jdbcType=INTEGER}, #{createId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
       , #{updateId,jdbcType=INTEGER}, #{updateTime,jdbcType=TIMESTAMP})

mapper.delete:
update s_sys_permission
set delete_flag=1
where id = #{id}
