mapper.insert:
insert into s_sys_user(org_id, dept_no, username, password, nickname, user_no, phone, email, user_type,
                       photo, wx, qq, status, create_id, create_time,
                       update_id, update_time)
values ( #{orgId,jdbcType=INTEGER}, #{deptNo,jdbcType=VARCHAR}, #{username,jdbcType=VARCHAR}
       , #{password,jdbcType=VARCHAR}, #{nickname,jdbcType=VARCHAR}
       , #{userNo,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}
       , #{userType,jdbcType=INTEGER}
       , #{photo,jdbcType=VARCHAR}, #{wx,jdbcType=VARCHAR}, #{qq,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}
       , #{createId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
       , #{updateId,jdbcType=INTEGER}, #{updateTime,jdbcType=TIMESTAMP})

mapper.update:
update s_sys_user
set username=#{username}
where id = #{id}

mapper.delete:
update s_sys_user
set delete_flag=1
where id = #{id}
